    return(
        <div className="bg-purple-900 flex items-center justify-center rounded-lg w-3/4 h-screen justify-around">
            <div>
                <div className=" h-36 bg-green-400 text-purple-abyss rounded-lg h-80 p-4 justify-around flex flex-col w-fit">
                    <p className="text-lg underline">{title}</p>
                    <img className="border-4 border-purple-abyss" src={img}></img>
                    <p className="text-sm">{info}</p>
                </div>
            </div>
        </div>
    )
}



