function Card({title, img, info}){
    return( 
                <div className="card h-36 bg-green-400 text-purple-abyss  h-80 p-4 m-4 justify-around flex flex-col w-1/5  lg: p-8">
                    <div id="card-dot" className="bg-purple-600 relative flex-none"></div>
                    <div >

                        <p className=" text-lg underline relative">{title}</p>

                        <img className="border-4 border-purple-abyss" src={img}></img>

                        <p className="text-sm">{info}</p>

                    </div>
                </div>


    )
}



