import { Fragment } from "react";
import { useState } from "react";
import { Dispatch } from "redux";

import { animated, useSpring, } from '@react-spring/web';

var Dispatcher = require('redux').Dispatcher;
module.exports = new Dispatcher();
function NavItem({title, url}){
    return (
      <a href={url}>
      {title}
      </a>
      );
  }

  function Logo({logo, size}){
    return(
      <img 
      src={logo}
      width={size}
      height={size}
      />
    )
  }
  function NavLinks(){
    return(
    <div id="accordian-items" className="flex flex-col h-96 items-center justify-between">
        <NavItem title="Home" url=""/>
                    <NavItem title="Blogs" urls=""/>
                    <NavItem title="Reviews" urls=""/>
                    <NavItem title="Podcasts" urls=""/>
                    <NavItem title="Login" urls=""/>
    </div>
    )
  }

  export default function NavbarAccordian(){
    const [isActive, setIsActive] = useState(false);
    const styles = useSpring({
        from: {
          opacity: 0,
          y: '6%',
        },
        to: {
          opacity: 1,
          y: 0,
        },
      })

    const AnimatedNavLinks = animated(NavLinks)
    return (
        <Fragment>
            <div className="accordian">
                <div className="flex flex-row justify-between items-center m-4">
                    <div className="flex flex-row items-center">
                    <Logo logo={"https://u.cubeupload.com/bostonessary/phunkplain.png"} size={50}/>
                    <p className="m-2">the Phunk</p>
                    </div>
                    <div className="accordian-symbol flex justify-end" onClick={() => setIsActive(!isActive)}>
                        <img src="https://svgshare.com/i/t84.svg" width={50} height={50}/>
                    </div>
                </div>


                {isActive && <div >
                    <AnimatedNavLinks style={styles}/>
                </div>}




            </div>
        </Fragment>
    )
}
